export default {
  path: '/',

  children: [
    require('./Root/route').default,
    require('./Login/route').default,
    require('./NotFound/route').default,
  ],

  async action({ next }) {
    const route = await next()

    route.title = `Page - ${ route.title }`
    route.description = `Description`

    return route
  }
}
